e']['size'];            $file_type = $_FILES['profile_image']['type'];            $folder = "../images/offers_stores/";            $new_size = $file_size / 1024;            $offer_image_temp = $file;            move_uploaded_file($file_loc, $folder . $offer_image_temp);            $offer_image = $offer_image_temp;        }else {            $offer_image = $profile_image_old;        }        $details_id = '';        if(isset($_POST['details_id'])) {            foreach ($_POST['details_id'] as $d_id) {                $details_id .=$d_id.',';            }        }        $details_values = '';        if(isset($_POST['details_text'])) {            foreach ($_POST['details_text'] as $d_value) {                $details_values .=$d_value.',';            }        }        $details_id = substr($details_id, 0, -1);        $details_values = substr($details_values, 0, -1);        $store_qry =            "UPDATE  " . TBL . "offers_stores  SET store_id='" . $store_id . "', user_id='" . $user_id . "', offer_name='" . $offer_name . "'            , offer_description='" . $offer_description . "', offer_img='" . $offer_image . "', offer_price='" . $offer_price . "',            offer_discount='" . $offer_discount . "',offer_warranty='" . $offer_warranty . "',details_id='" . $details_id . "',details_values='" . $details_values . "', offer_tags='" . $offer_tags . "'    , offer_slug ='" . $offer_name_slug . "', seo_title ='" . $seo_title. "', seo_description ='" . $seo_description. "', seo_keywords ='" . $seo_keywords. "' WHERE offer_id = ".$offer_id;        $store_res = mysqli_query($conn,$store_qry);        if ($store_res) {            $_SESSION['status_msg'] = $BIZBOOK['UPDATE_SUCCESS'];            header('Location: admin-offers-store.php');            exit;        } else {            $_SESSION['status_msg'] = $BIZBOOK['ERROR'];            header('Location: admin-offers-store-edit.php?row=' . $offer_id);        }    }}else