: like_status},                    success: function (response) {                        $(".Animatedheartfunc" + listing_id).toggleClass('sav-act');                        if (response == 1) {                            var total_likes1 = parseInt(total_likes) + 1;                            $(".Animatedheartfunc" + listing_id).attr("data-for", total_likes1); //setter                            $(".like-content" + listing_id).html(total_likes1);                        } else {                            var total_likes1 = parseInt(total_likes) - 1;                            if (total_likes1 == -1) {                                var total_likes2 = 0;                            } else {                                var total_likes2 = total_likes1;                            }                            $(".Animatedheartfunc" + listing_id).attr("data-for", total_likes2); //setter                            $(".like-content" + listing_id).html(total_likes2);                        }                        //$(".ldelik").toggleClass('sav-act');                    }                });            }        } else {            window.location.href = "login.php";        }    });</script></body></html>